package models

import (
	"database/sql"
	"errors"
	"fmt"
	"log"
	"strings"
	"time"
)

// TransactionType represents the type of transaction
type TransactionType string

// Transaction types
const (
	Deposit    TransactionType = "deposit"
	Withdrawal TransactionType = "withdrawal"
	Transfer   TransactionType = "transfer"
)

// TransactionStatus represents the status of a transaction
type TransactionStatus string

// Transaction statuses
const (
	Pending   TransactionStatus = "pending"
	Completed TransactionStatus = "completed"
	Failed    TransactionStatus = "failed"
	Cancelled TransactionStatus = "cancelled"
)

// Transaction represents a financial transaction in the system
type Transaction struct {
	ID              int64            `json:"id"`
	AccountID       int64            `json:"account_id"`
	DestAccountID   sql.NullInt64    `json:"dest_account_id,omitempty"`
	Amount          float64          `json:"amount"`
	Type            TransactionType  `json:"type"`
	Status          TransactionStatus `json:"status"`
	Description     string           `json:"description"`
	ReferenceNumber string           `json:"reference_number"`
	CreatedAt       time.Time        `json:"created_at"`
	UpdatedAt       time.Time        `json:"updated_at"`
}

// Validate checks if the transaction data is valid
func (t *Transaction) Validate() error {
	// Check if amount is positive
	if t.Amount <= 0 {
		return errors.New("transaction amount must be greater than zero")
	}

	// Check if transaction type is valid
	if t.Type != Deposit && t.Type != Withdrawal && t.Type != Transfer {
		return errors.New("invalid transaction type")
	}

	// For transfers, destination account must be specified
	if t.Type == Transfer && !t.DestAccountID.Valid {
		return errors.New("destination account is required for transfers")
	}

	// Check if transaction status is valid
	if t.Status != Pending && t.Status != Completed && t.Status != Failed && t.Status != Cancelled {
		return errors.New("invalid transaction status")
	}

	// Validate description (optional but if provided should not be too long)
	if len(t.Description) > 255 {
		return errors.New("description is too long (maximum 255 characters)")
	}

	return nil
}

// Create inserts a new transaction into the database
func (t *Transaction) Create(db *sql.DB) error {
	// Validate transaction data before insertion
	if err := t.Validate(); err != nil {
		return err
	}

	// Generate reference number if not provided
	if t.ReferenceNumber == "" {
		t.ReferenceNumber = generateReferenceNumber()
	}

	// Set timestamps
	now := time.Now()
	t.CreatedAt = now
	t.UpdatedAt = now

	// Prepare SQL statement
	query := `
		INSERT INTO transactions 
		(account_id, dest_account_id, amount, type, status, description, reference_number, created_at, updated_at) 
		VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)
		RETURNING id
	`

	// Execute the query
	var destAccountID interface{}
	if t.DestAccountID.Valid {
		destAccountID = t.DestAccountID.Int64
	} else {
		destAccountID = nil
	}

	// Log the transaction creation attempt (mask sensitive data)
	log.Printf("Creating transaction: type=%s, amount=%.2f, account_id=%d, status=%s", 
		t.Type, t.Amount, t.AccountID, t.Status)

	err := db.QueryRow(
		query,
		t.AccountID,
		destAccountID,
		t.Amount,
		t.Type,
		t.Status,
		t.Description,
		t.ReferenceNumber,
		t.CreatedAt,
		t.UpdatedAt,
	).Scan(&t.ID)

	if err != nil {
		log.Printf("Error creating transaction: %v", err)
		return fmt.Errorf("failed to create transaction: %w", err)
	}

	log.Printf("Transaction created successfully: id=%d, reference=%s", t.ID, t.ReferenceNumber)
	return nil
}

// GetByID retrieves a transaction by its ID
func (t *Transaction) GetByID(db *sql.DB, id int64) error {
	query := `
		SELECT id, account_id, dest_account_id, amount, type, status, description, 
		       reference_number, created_at, updated_at 
		FROM transactions 
		WHERE id = ?
	`

	var destAccountID sql.NullInt64
	err := db.QueryRow(query, id).Scan(
		&t.ID,
		&t.AccountID,
		&destAccountID,
		&t.Amount,
		&t.Type,
		&t.Status,
		&t.Description,
		&t.ReferenceNumber,
		&t.CreatedAt,
		&t.UpdatedAt,
	)

	if err != nil {
		if err == sql.ErrNoRows {
			return fmt.Errorf("transaction with ID %d not found", id)
		}
		return fmt.Errorf("error retrieving transaction: %w", err)
	}

	t.DestAccountID = destAccountID
	return nil
}

// GetByReferenceNumber retrieves a transaction by its reference number
func (t *Transaction) GetByReferenceNumber(db *sql.DB, refNumber string) error {
	query := `
		SELECT id, account_id, dest_account_id, amount, type, status, description, 
		       reference_number, created_at, updated_at 
		FROM transactions 
		WHERE reference_number = ?
	`

	var destAccountID sql.NullInt64
	err := db.QueryRow(query, refNumber).Scan(
		&t.ID,
		&t.AccountID,
		&destAccountID,
		&t.Amount,
		&t.Type,
		&t.Status,
		&t.Description,
		&t.ReferenceNumber,
		&t.CreatedAt,
		&t.UpdatedAt,
	)

	if err != nil {
		if err == sql.ErrNoRows {
			return fmt.Errorf("transaction with reference number %s not found", refNumber)
		}
		return fmt.Errorf("error retrieving transaction: %w", err)
	}

	t.DestAccountID = destAccountID
	return nil
}

// UpdateStatus updates the status of a transaction
func (t *Transaction) UpdateStatus(db *sql.DB, status TransactionStatus) error {
	// Validate the new status
	if status != Pending && status != Completed && status != Failed && status != Cancelled {
		return errors.New("invalid transaction status")
	}

	// Update the status and timestamp
	t.Status = status
	t.UpdatedAt = time.Now()

	query := `
		UPDATE transactions 
		SET status = ?, updated_at = ? 
		WHERE id = ?
	`

	log.Printf("Updating transaction status: id=%d, new_status=%s", t.ID, status)

	_, err := db.Exec(query, t.Status, t.UpdatedAt, t.ID)
	if err != nil {
		log.Printf("Error updating transaction status: %v", err)
		return fmt.Errorf("failed to update transaction status: %w", err)
	}

	log.Printf("Transaction status updated successfully: id=%d, status=%s", t.ID, t.Status)
	return nil
}

// GetTransactionsByAccountID retrieves all transactions for a specific account
func GetTransactionsByAccountID(db *sql.DB, accountID int64) ([]Transaction, error) {
	query := `
		SELECT id, account_id, dest_account_id, amount, type, status, description, 
		       reference_number, created_at, updated_at 
		FROM transactions 
		WHERE account_id = ? OR dest_account_id = ?
		ORDER BY created_at DESC
	`

	rows, err := db.Query(query, accountID, accountID)
	if err != nil {
		return nil, fmt.Errorf("error retrieving transactions: %w", err)
	}
	defer rows.Close()

	var transactions []Transaction
	for rows.Next() {
		var t Transaction
		var destAccountID sql.NullInt64

		err := rows.Scan(
			&t.ID,
			&t.AccountID,
			&destAccountID,
			&t.Amount,
			&t.Type,
			&t.Status,
			&t.Description,
			&t.ReferenceNumber,
			&t.CreatedAt,
			&t.UpdatedAt,
		)
		if err != nil {
			return nil, fmt.Errorf("error scanning transaction row: %w", err)
		}

		t.DestAccountID = destAccountID
		transactions = append(transactions, t)
	}

	if err = rows.Err(); err != nil {
		return nil, fmt.Errorf("error iterating transaction rows: %w", err)
	}

	log.Printf("Retrieved %d transactions for account ID %d", len(transactions), accountID)
	return transactions, nil
}

// GetTransactionsByDateRange retrieves transactions within a specific date range
func GetTransactionsByDateRange(db *sql.DB, accountID int64, startDate, endDate time.Time) ([]Transaction, error) {
	query := `
		SELECT id, account_id, dest_account_id, amount, type, status, description, 
		       reference_number, created_at, updated_at 
		FROM transactions 
		WHERE (account_id = ? OR dest_account_id = ?) 
		  AND created_at BETWEEN ? AND ?
		ORDER BY created_at DESC
	`

	rows, err := db.Query(query, accountID, accountID, startDate, endDate)
	if err != nil {
		return nil, fmt.Errorf("error retrieving transactions by date range: %w", err)
	}
	defer rows.Close()

	var transactions []Transaction
	for rows.Next() {
		var t Transaction
		var destAccountID sql.NullInt64

		err := rows.Scan(
			&t.ID,
			&t.AccountID,
			&destAccountID,
			&t.Amount,
			&t.Type,
			&t.Status,
			&t.Description,
			&t.ReferenceNumber,
			&t.CreatedAt,
			&t.UpdatedAt,
		)
		if err != nil {
			return nil, fmt.Errorf("error scanning transaction row: %w", err)
		}

		t.DestAccountID = destAccountID
		transactions = append(transactions, t)
	}

	if err = rows.Err(); err != nil {
		return nil, fmt.Errorf("error iterating transaction rows: %w", err)
	}

	log.Printf("Retrieved %d transactions for account ID %d between %s and %s", 
		len(transactions), accountID, startDate.Format("2006-01-02"), endDate.Format("2006-01-02"))
	return transactions, nil
}

// generateReferenceNumber creates a unique reference number for a transaction
func generateReferenceNumber() string {
	timestamp := time.Now().UnixNano() / int64(time.Millisecond)
	return fmt.Sprintf("TXN%d", timestamp)
}

// FormatAmount returns a formatted string representation of the transaction amount
func (t *Transaction) FormatAmount() string {
	return fmt.Sprintf("$%.2f", t.Amount)
}

// IsIncoming determines if this is an incoming transaction for the specified account
func (t *Transaction) IsIncoming(accountID int64) bool {
	return t.Type == Deposit || (t.Type == Transfer && t.DestAccountID.Valid && t.DestAccountID.Int64 == accountID)
}

// GetDescription returns a user-friendly description of the transaction
func (t *Transaction) GetDescription() string {
	if t.Description != "" {
		return t.Description
	}

	// Generate a default description based on transaction type
	switch t.Type {
	case Deposit:
		return "Deposit"
	case Withdrawal:
		return "Withdrawal"
	case Transfer:
		if t.DestAccountID.Valid {
			return fmt.Sprintf("Transfer to account #%d", t.DestAccountID.Int64)
		}
		return "Transfer"
	default:
		return string(t.Type)
	}
}

// GetFormattedDate returns a formatted date string for the transaction
func (t *Transaction) GetFormattedDate() string {
	return t.CreatedAt.Format("Jan 02, 2006 15:04:05")
}

// GetStatusColor returns a color code based on the transaction status
func (t *Transaction) GetStatusColor() string {
	switch t.Status {
	case Completed:
		return "green"
	case Pending:
		return "orange"
	case Failed:
		return "red"
	case Cancelled:
		return "gray"
	default:
		return "black"
	}
}

package utils

import (
	"crypto/rand"
	"crypto/subtle"
	"encoding/base64"
	"errors"
	"fmt"
	"log"
	"strings"

	"golang.org/x/crypto/argon2"
)

// PasswordConfig holds the parameters for the Argon2id password hashing algorithm
type PasswordConfig struct {
	Memory      uint32
	Iterations  uint32
	Parallelism uint8
	SaltLength  uint32
	KeyLength   uint32
}

// DefaultPasswordConfig returns a default configuration for the Argon2id algorithm
func DefaultPasswordConfig() *PasswordConfig {
	return &PasswordConfig{
		Memory:      64 * 1024, // 64MB
		Iterations:  3,
		Parallelism: 2,
		SaltLength:  16,
		KeyLength:   32,
	}
}

// HashPassword generates a hash of the provided password using the Argon2id algorithm
func HashPassword(password string, config *PasswordConfig) (string, error) {
	if password == "" {
		log.Println("Error: Empty password provided for hashing")
		return "", errors.New("password cannot be empty")
	}

	if config == nil {
		log.Println("Using default password configuration")
		config = DefaultPasswordConfig()
	}

	// Generate a random salt
	salt := make([]byte, config.SaltLength)
	if _, err := rand.Read(salt); err != nil {
		log.Printf("Error generating salt: %v", err)
		return "", fmt.Errorf("error generating salt: %w", err)
	}

	// Hash the password using Argon2id
	hash := argon2.IDKey(
		[]byte(password),
		salt,
		config.Iterations,
		config.Memory,
		config.Parallelism,
		config.KeyLength,
	)

	// Encode the salt and hash as base64
	b64Salt := base64.RawStdEncoding.EncodeToString(salt)
	b64Hash := base64.RawStdEncoding.EncodeToString(hash)

	// Format the hash string
	encodedHash := fmt.Sprintf(
		"$argon2id$v=%d$m=%d,t=%d,p=%d$%s$%s",
		argon2.Version,
		config.Memory,
		config.Iterations,
		config.Parallelism,
		b64Salt,
		b64Hash,
	)

	log.Println("Password hashed successfully")
	return encodedHash, nil
}

// VerifyPassword checks if the provided password matches the stored hash
func VerifyPassword(password, encodedHash string) (bool, error) {
	if password == "" || encodedHash == "" {
		log.Println("Error: Empty password or hash provided for verification")
		return false, errors.New("password and hash cannot be empty")
	}

	// Parse the hash string
	vals := strings.Split(encodedHash, "$")
	if len(vals) != 6 {
		log.Println("Error: Invalid hash format")
		return false, errors.New("invalid hash format")
	}

	var version int
	_, err := fmt.Sscanf(vals[2], "v=%d", &version)
	if err != nil {
		log.Printf("Error parsing hash version: %v", err)
		return false, fmt.Errorf("invalid hash version: %w", err)
	}

	if version != argon2.Version {
		log.Printf("Error: Incompatible argon2id version: %d", version)
		return false, fmt.Errorf("incompatible argon2id version: %d", version)
	}

	// Parse the parameters
	var memory, iterations uint32
	var parallelism uint8
	_, err = fmt.Sscanf(vals[3], "m=%d,t=%d,p=%d", &memory, &iterations, &parallelism)
	if err != nil {
		log.Printf("Error parsing hash parameters: %v", err)
		return false, fmt.Errorf("invalid hash parameters: %w", err)
	}

	// Decode the salt and hash
	salt, err := base64.RawStdEncoding.DecodeString(vals[4])
	if err != nil {
		log.Printf("Error decoding salt: %v", err)
		return false, fmt.Errorf("invalid salt encoding: %w", err)
	}

	decodedHash, err := base64.RawStdEncoding.DecodeString(vals[5])
	if err != nil {
		log.Printf("Error decoding hash: %v", err)
		return false, fmt.Errorf("invalid hash encoding: %w", err)
	}

	// Compute the hash of the provided password
	computedHash := argon2.IDKey(
		[]byte(password),
		salt,
		iterations,
		memory,
		parallelism,
		uint32(len(decodedHash)),
	)

	// Compare the computed hash with the stored hash
	match := subtle.ConstantTimeCompare(decodedHash, computedHash) == 1
	if match {
		log.Println("Password verification successful")
	} else {
		log.Println("Password verification failed")
	}

	return match, nil
}

// GenerateRandomPassword generates a random password of the specified length
func GenerateRandomPassword(length int) (string, error) {
	if length < 8 {
		log.Println("Error: Password length too short")
		return "", errors.New("password length must be at least 8 characters")
	}

	const charset = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*()-_=+[]{}|;:,.<>?"
	charsetLength := len(charset)

	// Generate random bytes
	randomBytes := make([]byte, length)
	_, err := rand.Read(randomBytes)
	if err != nil {
		log.Printf("Error generating random bytes: %v", err)
		return "", fmt.Errorf("error generating random bytes: %w", err)
	}

	// Convert random bytes to password characters
	password := make([]byte, length)
	for i, b := range randomBytes {
		password[i] = charset[int(b)%charsetLength]
	}

	log.Println("Random password generated successfully")
	return string(password), nil
}

// IsPasswordStrong checks if a password meets strength requirements
func IsPasswordStrong(password string) (bool, string) {
	if len(password) < 8 {
		msg := "Password must be at least 8 characters long"
		log.Println("Password strength check failed: " + msg)
		return false, msg
	}

	hasUpper := false
	hasLower := false
	hasDigit := false
	hasSpecial := false

	for _, char := range password {
		switch {
		case 'A' <= char && char <= 'Z':
			hasUpper = true
		case 'a' <= char && char <= 'z':
			hasLower = true
		case '0' <= char && char <= '9':
			hasDigit = true
		case strings.ContainsRune("!@#$%^&*()-_=+[]{}|;:,.<>?", char):
			hasSpecial = true
		}
	}

	if !hasUpper {
		msg := "Password must contain at least one uppercase letter"
		log.Println("Password strength check failed: " + msg)
		return false, msg
	}

	if !hasLower {
		msg := "Password must contain at least one lowercase letter"
		log.Println("Password strength check failed: " + msg)
		return false, msg
	}

	if !hasDigit {
		msg := "Password must contain at least one digit"
		log.Println("Password strength check failed: " + msg)
		return false, msg
	}

	if !hasSpecial {
		msg := "Password must contain at least one special character"
		log.Println("Password strength check failed: " + msg)
		return false, msg
	}

	log.Println("Password strength check passed")
	return true, ""
}

