// CustomClaims extends standard JWT claims with user-specific data
type CustomClaims struct {
	UserID   int64  `json:"user_id"`
	Username string `json:"username"`
	Email    string `json:"email"`
	jwt.RegisteredClaims
}

// JWTConfig holds JWT configuration
type JWTConfig struct {
	SecretKey     string
	TokenDuration time.Duration
}

// DefaultJWTConfig returns a default JWT configuration
func DefaultJWTConfig() JWTConfig {
	return JWTConfig{
		SecretKey:     "your-secret-key-change-in-production", // Should be loaded from environment or config
		TokenDuration: 24 * time.Hour,                         // 24 hours by default
	}
}

// GenerateToken creates a new JWT token for a user
func GenerateToken(userID int64, username, email string, config JWTConfig) (string, error) {
	log.Printf("Generating token for user: %d", userID) // Log user ID only, not PII

	// Create the claims
	claims := CustomClaims{
		UserID:   userID,
		Username: username,
		Email:    email,
		RegisteredClaims: jwt.RegisteredClaims{
			ExpiresAt: jwt.NewNumericDate(time.Now().Add(config.TokenDuration)),
			IssuedAt:  jwt.NewNumericDate(time.Now()),
			NotBefore: jwt.NewNumericDate(time.Now()),
			Issuer:    "api",
			Subject:   fmt.Sprintf("%d", userID),
		},
	}

	// Create token with claims
	token := jwt.NewWithClaims(jwt.SigningMethodHS256, claims)

	// Generate encoded token
	tokenString, err := token.SignedString([]byte(config.SecretKey))
	if err != nil {
		log.Printf("Error generating token: %v", err)
		return "", fmt.Errorf("failed to sign token: %w", err)
	}

	log.Printf("Token generated successfully for user: %d", userID)
	return tokenString, nil
}

// ValidateToken validates a JWT token and returns the claims
func ValidateToken(tokenString string, config JWTConfig) (*CustomClaims, error) {
	log.Println("Validating token")

	// Parse the token
	token, err := jwt.ParseWithClaims(tokenString, &CustomClaims{}, func(token *jwt.Token) (interface{}, error) {
		// Validate the signing method
		if _, ok := token.Method.(*jwt.SigningMethodHMAC); !ok {
			log.Printf("Unexpected signing method: %v", token.Header["alg"])
			return nil, ErrInvalidSignMethod
		}
		return []byte(config.SecretKey), nil
	})

	if err != nil {
		if errors.Is(err, jwt.ErrTokenExpired) {
			log.Println("Token validation failed: token expired")
			return nil, ErrExpiredToken
		}
		log.Printf("Token validation failed: %v", err)
		return nil, ErrInvalidToken
	}

	// Check if the token is valid
	if !token.Valid {
		log.Println("Token validation failed: invalid token")
		return nil, ErrInvalidToken
	}

	// Extract claims
	claims, ok := token.Claims.(*CustomClaims)
	if !ok {
		log.Println("Token validation failed: invalid claims type")
		return nil, ErrInvalidClaims
	}

	// Validate required claims
	if claims.UserID == 0 || claims.Username == "" || claims.Email == "" {
		log.Println("Token validation failed: missing required claims")
		return nil, ErrMissingClaims
	}

	log.Printf("Token validated successfully for user: %d", claims.UserID)
	return claims, nil
}

// RefreshToken generates a new token with extended expiration
func RefreshToken(tokenString string, config JWTConfig) (string, error) {
	log.Println("Refreshing token")

	// Validate the existing token
	claims, err := ValidateToken(tokenString, config)
	if err != nil {
		// Allow refreshing expired tokens, but not invalid ones
		if !errors.Is(err, ErrExpiredToken) {
			log.Printf("Token refresh failed: %v", err)
			return "", err
		}

		// For expired tokens, we need to parse without validation
		token, parseErr := jwt.ParseWithClaims(tokenString, &CustomClaims{}, func(token *jwt.Token) (interface{}, error) {
			return []byte(config.SecretKey), nil
		})

		if parseErr != nil {
			log.Printf("Failed to parse expired token: %v", parseErr)
			return "", ErrInvalidToken
		}

		var ok bool
		claims, ok = token.Claims.(*CustomClaims)
		if !ok {
			log.Println("Token refresh failed: invalid claims type")
			return "", ErrInvalidClaims
		}
	}

	// Generate a new token with the same claims but extended expiration
	return GenerateToken(claims.UserID, claims.Username, claims.Email, config)
}

// ExtractUserIDFromToken extracts the user ID from a token string
func ExtractUserIDFromToken(tokenString string, config JWTConfig) (int64, error) {
	claims, err := ValidateToken(tokenString, config)
	if err != nil {
		return 0, err
	}
	return claims.UserID, nil
}

// GetTokenFromHeader extracts the token from the Authorization header
func GetTokenFromHeader(authHeader string) (string, error) {
	if authHeader == "" {
		return "", errors.New("authorization header is missing")
	}

	// Check if the header has the Bearer prefix
	const prefix = "Bearer "
	if len(authHeader) <= len(prefix) || authHeader[:len(prefix)] != prefix {
		return "", errors.New("authorization header format must be Bearer {token}")
	}

	// Extract the token
	token := authHeader[len(prefix):]
	return token, nil
}

package db

import (
	"database/sql"
	"errors"
	"fmt"
	"log"
	"sync"
	"time"

	"github.com/go-sql-driver/mysql"
	_ "github.com/go-sql-driver/mysql"
	"github.com/jmoiron/sqlx"

	"backend/config"
)

// DB errors
var (
	ErrDBNotInitialized = errors.New("database not initialized")
	ErrNoRowsAffected   = errors.New("no rows affected")
	ErrInvalidConfig    = errors.New("invalid database configuration")
)

// DBManager handles database connections and operations
type DBManager struct {
	db         *sqlx.DB
	config     *config.DatabaseConfig
	mu         sync.RWMutex
	isConnected bool
}

// NewDBManager creates a new database manager instance
func NewDBManager(cfg *config.DatabaseConfig) (*DBManager, error) {
	if cfg == nil {
		return nil, ErrInvalidConfig
	}

	return &DBManager{
		config: cfg,
	}, nil
}

// Connect establishes a connection to the database
func (m *DBManager) Connect() error {
	m.mu.Lock()
	defer m.mu.Unlock()

	if m.isConnected {
		return nil
	}

	// Configure connection parameters
	mysqlConfig := mysql.Config{
		User:                 m.config.Username,
		Passwd:               m.config.Password,
		Net:                  "tcp",
		Addr:                 fmt.Sprintf("%s:%d", m.config.Host, m.config.Port),
		DBName:               m.config.Database,
		AllowNativePasswords: true,
		ParseTime:            true,
		Loc:                  time.UTC,
		Timeout:              time.Second * 10,
		ReadTimeout:          time.Second * 30,
		WriteTimeout:         time.Second * 30,
	}

	// Create the connection string
	connStr := mysqlConfig.FormatDSN()

	// Connect to the database
	db, err := sqlx.Connect("mysql", connStr)
	if err != nil {
		return fmt.Errorf("failed to connect to database: %w", err)
	}

	// Configure connection pool
	db.SetMaxOpenConns(m.config.MaxOpenConns)
	db.SetMaxIdleConns(m.config.MaxIdleConns)
	db.SetConnMaxLifetime(time.Duration(m.config.ConnMaxLifetimeMinutes) * time.Minute)

	// Test the connection
	if err := db.Ping(); err != nil {
		return fmt.Errorf("failed to ping database: %w", err)
	}

	m.db = db
	m.isConnected = true
	log.Printf("Successfully connected to database %s at %s:%d", m.config.Database, m.config.Host, m.config.Port)
	return nil
}

// Disconnect closes the database connection
func (m *DBManager) Disconnect() error {
	m.mu.Lock()
	defer m.mu.Unlock()

	if m.db == nil {
		return nil
	}

	err := m.db.Close()
	if err != nil {
		return fmt.Errorf("error closing database connection: %w", err)
	}

	m.db = nil
	m.isConnected = false
	log.Println("Database connection closed")
	return nil
}

// IsConnected returns the connection status
func (m *DBManager) IsConnected() bool {
	m.mu.RLock()
	defer m.mu.RUnlock()
	return m.isConnected
}

// GetDB returns the database connection
func (m *DBManager) GetDB() (*sqlx.DB, error) {
	m.mu.RLock()
	defer m.mu.RUnlock()

	if m.db == nil {
		return nil, ErrDBNotInitialized
	}

	// Check if connection is still alive
	if err := m.db.Ping(); err != nil {
		m.mu.RUnlock() // Unlock before attempting to reconnect
		log.Println("Database connection lost, attempting to reconnect...")

		// Need to acquire write lock for reconnection
		m.mu.Lock()
		defer m.mu.Lock()

		m.isConnected = false
		if err := m.Connect(); err != nil {
			return nil, fmt.Errorf("failed to reconnect to database: %w", err)
		}
	}

	return m.db, nil
}

// ExecuteTransaction executes a function within a transaction
func (m *DBManager) ExecuteTransaction(fn func(*sqlx.Tx) error) error {
	db, err := m.GetDB()
	if err != nil {
		return err
	}

	tx, err := db.Beginx()
	if err != nil {
		return fmt.Errorf("failed to begin transaction: %w", err)
	}

	// Execute the function within the transaction
	if err := fn(tx); err != nil {
		// Attempt to rollback on error
		if rbErr := tx.Rollback(); rbErr != nil {
			return fmt.Errorf("error in transaction: %v, rollback failed: %w", err, rbErr)
		}
		return err
	}

	// Commit the transaction
	if err := tx.Commit(); err != nil {
		return fmt.Errorf("failed to commit transaction: %w", err)
	}

	return nil
}

// CheckRowsAffected checks if any rows were affected by the query
func (m *DBManager) CheckRowsAffected(result sql.Result) error {
	if result == nil {
		return errors.New("nil result")
	}

	rowsAffected, err := result.RowsAffected()
	if err != nil {
		return fmt.Errorf("error checking rows affected: %w", err)
	}

	if rowsAffected == 0 {
		return ErrNoRowsAffected
	}

	return nil
}

// Ping checks if the database connection is alive
func (m *DBManager) Ping() error {
	db, err := m.GetDB()
	if err != nil {
		return err
	}

	return db.Ping()
}

// MigrateDB performs database migrations
func (m *DBManager) MigrateDB() error {
	db, err := m.GetDB()
	if err != nil {
		return err
	}

	// Create tables if they don't exist
	migrations := []string{
		// Users table
		`CREATE TABLE IF NOT EXISTS users (
			id INT AUTO_INCREMENT PRIMARY KEY,
			username VARCHAR(50) NOT NULL UNIQUE,
			email VARCHAR(100) NOT NULL UNIQUE,
			password_hash VARCHAR(255) NOT NULL,
			created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
			updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
			INDEX idx_username (username),
			INDEX idx_email (email)
		)`,

		// Add more table creation statements as needed
	}

	for _, migration := range migrations {
		_, err := db.Exec(migration)
		if err != nil {
			return fmt.Errorf("migration failed: %w", err)
		}
	}

	log.Println("Database migrations completed successfully")
	return nil
}

package middleware

import (
	"context"
	"log"
	"net/http"
	"strings"

	"github.com/golang-jwt/jwt/v4"

	"backend/utils"
)

// contextKey is a custom type for context keys to avoid collisions
type contextKey string

// UserIDKey is the key used to store the user ID in the request context
const UserIDKey contextKey = "userID"

// AuthMiddleware is a middleware that validates JWT tokens and extracts user information
func AuthMiddleware(next http.Handler) http.Handler {
	return http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
		log.Println("Processing request through auth middleware")

		// Skip auth for certain paths (like login, register)
		if isExemptPath(r.URL.Path) {
			log.Printf("Skipping auth for exempt path: %s", r.URL.Path)
			next.ServeHTTP(w, r)
			return
		}

		// Extract token from Authorization header
		tokenString, err := extractTokenFromHeader(r)
		if err != nil {
			log.Printf("Failed to extract token: %v", err)
			http.Error(w, "Unauthorized: Invalid or missing token", http.StatusUnauthorized)
			return
		}

		// Validate the token
		claims, err := validateToken(tokenString)
		if err != nil {
			log.Printf("Token validation failed: %v", err)
			http.Error(w, "Unauthorized: Invalid token", http.StatusUnauthorized)
			return
		}

		// Extract user ID from claims
		userID, ok := claims["user_id"].(string)
		if !ok {
			log.Printf("Failed to extract user_id from token claims")
			http.Error(w, "Unauthorized: Invalid token claims", http.StatusUnauthorized)
			return
		}

		// Add user ID to request context
		ctx := context.WithValue(r.Context(), UserIDKey, userID)
		log.Printf("User authenticated: %s", maskPII(userID))

		// Call the next handler with the updated context
		next.ServeHTTP(w, r.WithContext(ctx))
	})
}

// GetUserIDFromContext extracts the user ID from the request context
func GetUserIDFromContext(ctx context.Context) (string, error) {
	userID, ok := ctx.Value(UserIDKey).(string)
	if !ok {
		return "", errors.New("user ID not found in context")
	}
	return userID, nil
}

// isExemptPath checks if the given path is exempt from authentication
func isExemptPath(path string) bool {
	exemptPaths := []string{
		"/api/auth/login",
		"/api/auth/register",
		"/api/health",
		"/api/docs",
	}

	for _, exemptPath := range exemptPaths {
		if strings.HasPrefix(path, exemptPath) {
			return true
		}
	}
	return false
}

// extractTokenFromHeader extracts the JWT token from the Authorization header
func extractTokenFromHeader(r *http.Request) (string, error) {
	return utils.ExtractTokenFromHeader(r)
}

// validateToken validates the JWT token and returns the claims
func validateToken(tokenString string) (jwt.MapClaims, error) {
	token, err := jwt.Parse(tokenString, func(token *jwt.Token) (interface{}, error) {
		// Validate the signing method
		if _, ok := token.Method.(*jwt.SigningMethodHMAC); !ok {
			return nil, errors.New("unexpected signing method")
		}

		// Return the secret key used for signing
		return []byte(utils.GetJWTSecret()), nil
	})

	if err != nil {
		return nil, err
	}

	if !token.Valid {
		return nil, errors.New("invalid token")
	}

	claims, ok := token.Claims.(jwt.MapClaims)
	if !ok {
		return nil, errors.New("invalid token claims")
	}

	return claims, nil
}

// maskPII masks personally identifiable information for logging
func maskPII(data string) string {
	if len(data) <= 4 {
		return "****"
	}

	// Keep first 2 and last 2 characters, mask the rest
	return data[:2] + strings.Repeat("*", len(data)-4) + data[len(data)-2:]
}

