        return 'Checking Account';
      case AccountType.savings:
        return 'Savings Account';
      case AccountType.investment:
        return 'Investment Account';
      case AccountType.credit:
        return 'Credit Card';
      case AccountType.loan:
        return 'Loan Account';
      default:
        return 'Account';
